 *
 * myClass.fn(); // => undefined
 * myClass.fn(); // => 10
 *
 * myClass2.fn(); // => 10
 * myClass2.fn(); // => 10
 */
export function AfterAll(n) {
    return decorator(n);
}
export { AfterAll as afterAll };
export default decorator;

