 *
 * const myClass = new MyClass();
 *
 * myClass.fn();
 * myClass.fn();
 * myClass.fn();
 * myClass.fn();
 *
 * calls === 2; // => true
 */
export function Before(n) {
    return decorator(n);
}
export { Before as before };
export default decorator;

